","keccak256":"0xfcd8d3b052c8e023b0db72cb9afe55c552d5a1ef4741dbcd1a494145f2d5ff62","license":"BUSL-1.1"},"contracts/libraries/liquidityCalcs.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { LibsErrorTypes as ErrorTypes } from "./errorTypes.sol";
import { LiquiditySlotsLink } from "./liquiditySlotsLink.sol";
import { BigMathMinified } from "./bigMathMinified.sol";

/// @notice implements calculation methods used for Fluid liquidity such as updated exchange prices,
/// borrow rate, withdrawal / borrow limits, revenue amount.
library LiquidityCalcs {
    error FluidLiquidityCalcsError(uint256 errorId_);

    /// @notice emitted if the calculated borrow rate surpassed max borrow rate (16 bits) and was capped at maximum value 65535
    event BorrowRateMaxCap();

    /// @dev constants as from Liquidity variables.sol
    uint256 internal constant EXCHANGE_PRICES_PRECISION = 1e12;

    /// @dev Ignoring leap years
    uint256 internal constant SECONDS_PER_YEAR = 365 days;
    // constants used for BigMath conversion from and to storage
    uint256 internal constant DEFAULT_EXPONENT_SIZE = 8;
    uint256 internal constant DEFAULT_EXPONENT_MASK = 0xFF;

    uint256 internal constant FOUR_DECIMALS = 1e4;
    uint256 internal constant TWELVE_DECIMALS = 1e12;
    uint256 internal constant X14 = 0x3fff;
    uint256 internal constant X15 = 0x7fff;
    uint256 internal constant X16 = 0xffff;
    uint256 internal constant X18 = 0x3ffff;
    uint256 internal constant X24 = 0xffffff;
    uint256 internal constant X33 = 0x1ffffffff;
    uint256 internal constant X64 = 0xffffffffffffffff;

    ///////////////////////////////////////////////////////////////////////////
    //////////                  CALC EXCHANGE PRICES                  /////////
    ///////////////////////////////////////////////////////////////////////////

    /// @dev calculates interest (exchange prices) for a token given its' exchangePricesAndConfig from storage.
    /// @param exchangePricesAndConfig_ exchange prices and config packed uint256 read from storage
    /// @return supplyExchangePrice_ updated supplyExchangePrice
    /// @return borrowExchangePrice_ updated borrowExchangePrice
    function calcExchangePrices(
        uint256 exchangePricesAndConfig_
    ) internal view returns (uint256 supplyExchangePrice_, uint256 borrowExchangePrice_) {
        // Extracting exchange prices
        supplyExchangePrice_ =
            (exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_SUPPLY_EXCHANGE_PRICE) &
            X64;
        borrowExchangePrice_ =
            (exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_BORROW_EXCHANGE_PRICE) &
            X64;

        if (supplyExchangePrice_ == 0 || borrowExchangePrice_ == 0) {
            revert FluidLiquidityCalcsError(ErrorTypes.LiquidityCalcs__ExchangePriceZero);
        }

        uint256 temp_ = exchangePricesAndConfig_ & X16; // temp_ = borrowRate

        unchecked {
            // last timestamp can not be > current timestamp
            uint256 secondsSinceLastUpdate_ = block.timestamp -
                ((exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_LAST_TIMESTAMP) & X33);

            uint256 borrowRatio_ = (exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_BORROW_RATIO) &
                X15;
            if (secondsSinceLastUpdate_ == 0 || temp_ == 0 || borrowRatio_ == 1) {
                // if no time passed, borrow rate is 0, or no raw borrowings: no exchange price update needed
                // (if borrowRatio_ == 1 means there is only borrowInterestFree, as first bit is 1 and rest is 0)
                return (supplyExchangePrice_, borrowExchangePrice_);
            }

            // calculate new borrow exchange price.
            // formula borrowExchangePriceIncrease: previous price * borrow rate * secondsSinceLastUpdate_.
            // nominator is max uint112 (uint64 * uint16 * uint32). Divisor can not be 0.
            borrowExchangePrice_ +=
                (borrowExchangePrice_ * temp_ * secondsSinceLastUpdate_) /
                (SECONDS_PER_YEAR * FOUR_DECIMALS);

            // FOR SUPPLY EXCHANGE PRICE:
            // all yield paid by borrowers (in mode with interest) goes to suppliers in mode with interest.
            // formula: previous price * supply rate * secondsSinceLastUpdate_.
            // where supply rate = (borrow rate  - revenueFee%) * ratioSupplyYield. And
            // ratioSupplyYield = utilization * supplyRatio * borrowRatio
            //
            // Example:
            // supplyRawInterest is 80, supplyInterestFree is 20. totalSupply is 100. BorrowedRawInterest is 50.
            // BorrowInterestFree is 10. TotalBorrow is 60. borrow rate 40%, revenueFee 10%.
            // yield is 10 (so half a year must have passed).
            // supplyRawInterest must become worth 89. totalSupply must become 109. BorrowedRawInterest must become 60.
            // borrowInterestFree must still be 10. supplyInterestFree still 20. totalBorrow 70.
            // supplyExchangePrice would have to go from 1 to 1,125 (+ 0.125). borrowExchangePrice from 1 to 1,2 (+0.2).
            // utilization is 60%. supplyRatio = 20 / 80 = 25% (only 80% of lenders receiving yield).
            // borrowRatio = 10 / 50 = 20% (only 83,333% of borrowers paying yield):
            // x of borrowers paying yield = 100% - (20 / (100 + 20)) = 100% - 16.6666666% = 83,333%.
            // ratioSupplyYield = 60% * 83,33333% * (100% + 20%) = 62,5%
            // supplyRate = (40% * (100% - 10%)) * = 36% * 62,5% = 22.5%
            // increase in supplyExchangePrice, assuming 100 as previous price.
            // 100 * 22,5% * 1/2 (half a year) = 0,1125.
            // cross-check supplyRawInterest worth = 80 * 1.1125 = 89. totalSupply worth = 89 + 20.

            // -------------- 1. calculate ratioSupplyYield --------------------------------
            // step1: utilization * supplyRatio (or actually part of lenders receiving yield)

            // temp_ => supplyRatio (in 1e2: 100% = 10_000; 1% = 100 -> max value 16_383)
            // if first bit 0 then ratio is supplyInterestFree / supplyWithInterest (supplyWithInterest is bigger)
            // else ratio is supplyWithInterest / supplyInterestFree (supplyInterestFree is bigger)
            temp_ = (exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_SUPPLY_RATIO) & X15;

            if (temp_ == 1) {
                // if no raw supply: no exchange price update needed
                // (if supplyRatio_ == 1 means there is only supplyInterestFree, as first bit is 1 and rest is 0)
                return (supplyExchangePrice_, borrowExchangePrice_);
            }

            // ratioSupplyYield precision is 1e27 as 100% for increased precision when supplyInterestFree > supplyWithInterest
            if (temp_ & 1 == 1) {
                // ratio is supplyWithInterest / supplyInterestFree (supplyInterestFree is bigger)
                temp_ = temp_ >> 1;

                // Note: case where temp_ == 0 (only supplyInterestFree, no yield) already covered by early return
                // in the if statement a little above.

                // based on above example but supplyRawInterest is 20, supplyInterestFree is 80. no fee.
                // supplyRawInterest must become worth 30. totalSupply must become 110.
                // supplyExchangePrice would have to go from 1 to 1,5. borrowExchangePrice from 1 to 1,2.
                // so ratioSupplyYield must come out as 2.5 (250%).
                // supplyRatio would be (20 * 10_000 / 80) = 2500. but must be inverted.
                temp_ = (1e27 * FOUR_DECIMALS) / temp_; // e.g. 1e31 / 2500 = 4e27. (* 1e27 for precision)
                // e.g. 5_000 * (1e27 + 4e27) / 1e27 = 25_000 (=250%).
                temp_ =
                    // utilization * (100% + 100% / supplyRatio)
                    (((exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_UTILIZATION) & X14) *
                        (1e27 + temp_)) / // extract utilization (max 16_383 so there is no way this can overflow).
                    (FOUR_DECIMALS);
                // max possible value of temp_ here is 16383 * (1e27 + 1e31) / 1e4 = ~1.64e31
            } else {
                // ratio is supplyInterestFree / supplyWithInterest (supplyWithInterest is bigger)
                temp_ = temp_ >> 1;
                // if temp_ == 0 then only supplyWithInterest => full yield. temp_ is already 0

                // e.g. 5_000 * 10_000 + (20 * 10_000 / 80) / 10_000 = 5000 * 12500 / 10000 = 6250 (=62.5%).
                temp_ =
                    // 1e27 * utilization * (100% + supplyRatio) / 100%
                    (1e27 *
                        ((exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_UTILIZATION) & X14) * // extract utilization (max 16_383 so there is no way this can overflow).
                        (FOUR_DECIMALS + temp_)) /
                    (FOUR_DECIMALS * FOUR_DECIMALS);
                // max possible temp_ value: 1e27 * 16383 * 2e4 / 1e8 = 3.2766e27
            }
            // from here temp_ => ratioSupplyYield (utilization * supplyRatio part) scaled by 1e27. max possible value ~1.64e31

            // step2 of ratioSupplyYield: add borrowRatio (only x% of borrowers paying yield)
            if (borrowRatio_ & 1 == 1) {
                // ratio is borrowWithInterest / borrowInterestFree (borrowInterestFree is bigger)
                borrowRatio_ = borrowRatio_ >> 1;
                // borrowRatio_ => x of total bororwers paying yield. scale to 1e27.

                // Note: case where borrowRatio_ == 0 (only borrowInterestFree, no yield) already covered
                // at the beginning of the method by early return if `borrowRatio_ == 1`.

                // based on above example but borrowRawInterest is 10, borrowInterestFree is 50. no fee. borrowRatio = 20%.
                // so only 16.66% of borrowers are paying yield. so the 100% - part of the formula is not needed.
                // x of borrowers paying yield = (borrowRatio / (100 + borrowRatio)) = 16.6666666%
                // borrowRatio_ => x of total bororwers paying yield. scale to 1e27.
                borrowRatio_ = (borrowRatio_ * 1e27) / (FOUR_DECIMALS + borrowRatio_);
                // max value here for borrowRatio_ is (1e31 / (1e4 + 1e4))= 5e26 (= 50% of borrowers paying yield).
            } else {
                // ratio is borrowInterestFree / borrowWithInterest (borrowWithInterest is bigger)
                borrowRatio_ = borrowRatio_ >> 1;

                // borrowRatio_ => x of total bororwers paying yield. scale to 1e27.
                // x of borrowers paying yield = 100% - (borrowRatio / (100 + borrowRatio)) = 100% - 16.6666666% = 83,333%.
                borrowRatio_ = (1e27 - ((borrowRatio_ * 1e27) / (FOUR_DECIMALS + borrowRatio_)));
                // borrowRatio can never be > 100%. so max subtraction can be 100% - 100% / 200%.
                // or if borrowRatio_ is 0 -> 100% - 0. or if borrowRatio_ is 1 -> 100% - 1 / 101.
                // max value here for borrowRatio_ is 1e27 - 0 = 1e27 (= 100% of borrowers paying yield).
            }

            // temp_ => ratioSupplyYield. scaled down from 1e25 = 1% each to normal percent precision 1e2 = 1%.
            // max nominator value is ~1.64e31 * 1e27 = 1.64e58. max result = 1.64e8
            temp_ = (FOUR_DECIMALS * temp_ * borrowRatio_) / 1e54;

            // 2. calculate supply rate
            // temp_ => supply rate (borrow rate  - revenueFee%) * ratioSupplyYield.
            // division part is done in next step to increase precision. (divided by 2x FOUR_DECIMALS, fee + borrowRate)
            // Note that all calculation divisions for supplyExchangePrice are rounded down.
            // Note supply rate can be bigger than the borrowRate, e.g. if there are only few lenders with interest
            // but more suppliers not earning interest.
            temp_ = ((exchangePricesAndConfig_ & X16) * // borrow rate
                temp_ * // ratioSupplyYield
                (FOUR_DECIMALS - ((exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_FEE) & X14))); // revenueFee
            // fee can not be > 100%. max possible = 65535 * ~1.64e8 * 1e4 =~1.074774e17.

            // 3. calculate increase in supply exchange price
            supplyExchangePrice_ += ((supplyExchangePrice_ * temp_ * secondsSinceLastUpdate_) /
                (SECONDS_PER_YEAR * FOUR_DECIMALS * FOUR_DECIMALS * FOUR_DECIMALS));
            // max possible nominator = max uint 64 * 1.074774e17 * max uint32 = ~8.52e45. Denominator can not be 0.
        }
    }

    ///////////////////////////////////////////////////////////////////////////
    //////////                     CALC REVENUE                       /////////
    ///////////////////////////////////////////////////////////////////////////

    /// @dev gets the `revenueAmount_` for a token given its' totalAmounts and exchangePricesAndConfig from storage
    /// and the current balance of the Fluid liquidity contract for the token.
    /// @param totalAmounts_ total amounts packed uint256 read from storage
    /// @param exchangePricesAndConfig_ exchange prices and config packed uint256 read from storage
    /// @param liquidityTokenBalance_   current balance of Liquidity contract (IERC20(token_).balanceOf(address(this)))
    /// @return revenueAmount_ collectable revenue amount
    function calcRevenue(
        uint256 totalAmounts_,
        uint256 exchangePricesAndConfig_,
        uint256 liquidityTokenBalance_
    ) internal view returns (uint256 revenueAmount_) {
        // @dev no need to super-optimize this method as it is only used by admin

        // calculate the new exchange prices based on earned interest
        (uint256 supplyExchangePrice_, uint256 borrowExchangePrice_) = calcExchangePrices(exchangePricesAndConfig_);

        // total supply = interest free + with interest converted from raw
        uint256 totalSupply_ = getTotalSupply(totalAmounts_, supplyExchangePrice_);

        if (totalSupply_ > 0) {
            // available revenue: balanceOf(token) + totalBorrowings - totalLendings.
            revenueAmount_ = liquidityTokenBalance_ + getTotalBorrow(totalAmounts_, borrowExchangePrice_);
            // ensure there is no possible case because of rounding etc. where this would revert,
            // explicitly check if >
            revenueAmount_ = revenueAmount_ > totalSupply_ ? revenueAmount_ - totalSupply_ : 0;
        } else {
            // if supply is 0, then rest of balance can be withdrawn as revenue so that no amounts get stuck
            revenueAmount_ = liquidityTokenBalance_;
        }
    }

    ///////////////////////////////////////////////////////////////////////////
    //////////                      CALC LIMITS                       /////////
    ///////////////////////////////////////////////////////////////////////////

    /// @dev calculates withdrawal limit before an operate execution:
    /// amount of user supply that must stay supplied (not amount that can be withdrawn).
    /// i.e. if user has supplied 100m and can withdraw 5M, this method returns the 95M, not the withdrawable amount 5M
    /// @param userSupplyData_ user supply data packed uint256 from storage
    /// @param userSupply_ current user supply amount already extracted from `userSupplyData_` and converted from BigMath
    /// @return currentWithdrawalLimit_ current withdrawal limit updated for expansion since last interaction.
    ///         returned value is in raw for with interest mode, normal amount for interest free mode!
    function calcWithdrawalLimitBeforeOperate(
        uint256 userSupplyData_,
        uint256 userSupply_
    ) internal view returns (uint256 currentWithdrawalLimit_) {
        // @dev must support handling the case where timestamp is 0 (config is set but no interactions yet).
        // first tx where timestamp is 0 will enter `if (lastWithdrawalLimit_ == 0)` because lastWithdrawalLimit_ is not set yet.
        // returning max withdrawal allowed, which is not exactly right but doesn't matter because the first interaction must be
        // a deposit anyway. Important is that it would not revert.

        // Note the first time a deposit brings the user supply amount to above the base withdrawal limit, the active limit
        // is the fully expanded limit immedaitely.

        // extract last set withdrawal limit
        uint256 lastWithdrawalLimit_ = (userSupplyData_ >>
            LiquiditySlotsLink.BITS_USER_SUPPLY_PREVIOUS_WITHDRAWAL_LIMIT) & X64;
        lastWithdrawalLimit_ =
            (lastWithdrawalLimit_ >> DEFAULT_EXPONENT_SIZE) <<
            (lastWithdrawalLimit_ & DEFAULT_EXPONENT_MASK);
        if (lastWithdrawalLimit_ == 0) {
            // withdrawal limit is not activated. Max withdrawal allowed
            return 0;
        }

        uint256 maxWithdrawableLimit_;
        uint256 temp_;
        unchecked {
            // extract max withdrawable percent of user supply and
            // calculate maximum withdrawable amount expandPercentage of user supply at full expansion duration elapsed
            // e.g.: if 10% expandPercentage, meaning 10% is withdrawable after full expandDuration has elapsed.

            // userSupply_ needs to be atleast 1e73 to overflow max limit of ~1e77 in uint256 (no token in existence where this is possible).
            maxWithdrawableLimit_ =
                (((userSupplyData_ >> LiquiditySlotsLink.BITS_USER_SUPPLY_EXPAND_PERCENT) & X14) * userSupply_) /
                FOUR_DECIMALS;

            // time elapsed since last withdrawal limit was set (in seconds)
            // @dev last process timestamp is guaranteed to exist for withdrawal, as a supply must have happened before.
            // last timestamp can not be > current timestamp
            temp_ =
                block.timestamp -
                ((userSupplyData_ >> LiquiditySlotsLink.BITS_USER_SUPPLY_LAST_UPDATE_TIMESTAMP) & X33);
        }
        // calculate withdrawable amount of expandPercent that is elapsed of expandDuration.
        // e.g. if 60% of expandDuration has elapsed, then user should be able to withdraw 6% of user supply, down to 94%.
        // Note: no explicit check for this needed, it is covered by setting minWithdrawalLimit_ if needed.
        temp_ =
            (maxWithdrawableLimit_ * temp_) /
            // extract expand duration: After this, decrement won't happen (user can withdraw 100% of withdraw limit)
            ((userSupplyData_ >> LiquiditySlotsLink.BITS_USER_SUPPLY_EXPAND_DURATION) & X24); // expand duration can never be 0
        // calculate expanded withdrawal limit: last withdrawal limit - withdrawable amount.
        // Note: withdrawable amount here can grow bigger than userSupply if timeElapsed is a lot bigger than expandDuration,
        // which would cause the subtraction `lastWithdrawalLimit_ - withdrawableAmount_` to revert. In that case, set 0
        // which will cause minimum withdrawal limit to be set in lines below.
        unchecked {
            // underflow explicitly checked & handled
            currentWithdrawalLimit_ = lastWithdrawalLimit_ > temp_ ? lastWithdrawalLimit_ - temp_ : 0;
            // calculate minimum withdrawal limit: minimum amount of user supply that must stay supplied at full expansion
            // subtraction can not underflow as maxWithdrawableLimit_ is a percentage amount (<100%) of userSupply_
            temp_ = userSupply_ - maxWithdrawableLimit_;
        }
        // if withdrawal limit is decreased below minimum then set minimum
        // (e.g. when more than expandDuration time has elapsed)
        if (temp_ > currentWithdrawalLimit_) {
            currentWithdrawalLimit_ = temp_;
        }
    }

    /// @dev calculates withdrawal limit after an operate execution:
    /// amount of user supply that must stay supplied (not amount that can be withdrawn).
    /// i.e. if user has supplied 100m and can withdraw 5M, this method returns the 95M, not the withdrawable amount 5M
    /// @param userSupplyData_ user supply data packed uint256 from storage
    /// @param userSupply_ current user supply amount already extracted from `userSupplyData_` and added / subtracted with the executed operate amount
    /// @param newWithdrawalLimit_ current withdrawal limit updated for expansion since last interaction, result from `calcWithdrawalLimitBeforeOperate`
    /// @return withdrawalLimit_ updated withdrawal limit that should be written to storage. returned value is in
    ///                          raw for with interest mode, normal amount for interest free mode!
    function calcWithdrawalLimitAfterOperate(
        uint256 userSupplyData_,
        uint256 userSupply_,
        uint256 newWithdrawalLimit_
    ) internal pure returns (uint256) {
        // temp_ => base withdrawal limit. below this, maximum withdrawals are allowed
        uint256 temp_ = (userSupplyData_ >> LiquiditySlotsLink.BITS_USER_SUPPLY_BASE_WITHDRAWAL_LIMIT) & X18;
        temp_ = (temp_ >> DEFAULT_EXPONENT_SIZE) << (temp_ & DEFAULT_EXPONENT_MASK);

        // if user supply is below base limit then max withdrawals are allowed
        if (userSupply_ < temp_) {
            return 0;
        }
        // temp_ => withdrawal limit expandPercent (is in 1e2 decimals)
        temp_ = (userSupplyData_ >> LiquiditySlotsLink.BITS_USER_SUPPLY_EXPAND_PERCENT) & X14;
        unchecked {
            // temp_ => minimum withdrawal limit: userSupply - max withdrawable limit (userSupply * expandPercent))
            // userSupply_ needs to be atleast 1e73 to overflow max limit of ~1e77 in uint256 (no token in existence where this is possible).
            // subtraction can not underflow as maxWithdrawableLimit_ is a percentage amount (<100%) of userSupply_
            temp_ = userSupply_ - ((userSupply_ * temp_) / FOUR_DECIMALS);
        }
        // if new (before operation) withdrawal limit is less than minimum limit then set minimum limit.
        // e.g. can happen on new deposits. withdrawal limit is instantly fully expanded in a scenario where
        // increased deposit amount outpaces withrawals.
        if (temp_ > newWithdrawalLimit_) {
            return temp_;
        }
        return newWithdrawalLimit_;
    }

    /// @dev calculates borrow limit before an operate execution:
    /// total amount user borrow can reach (not borrowable amount in current operation).
    /// i.e. if user has borrowed 50M and can still borrow 5M, this method returns the total 55M, not the borrowable amount 5M
    /// @param userBorrowData_ user borrow data packed uint256 from storage
    /// @param userBorrow_ current user borrow amount already extracted from `userBorrowData_`
    /// @return currentBorrowLimit_ current borrow limit updated for expansion since last interaction. returned value is in
    ///                             raw for with interest mode, normal amount for interest free mode!
    function calcBorrowLimitBeforeOperate(
        uint256 userBorrowData_,
        uint256 userBorrow_
    ) internal view returns (uint256 currentBorrowLimit_) {
        // @dev must support handling the case where timestamp is 0 (config is set but no interactions yet) -> base limit.
        // first tx where timestamp is 0 will enter `if (maxExpandedBorrowLimit_ < baseBorrowLimit_)` because `userBorrow_` and thus
        // `maxExpansionLimit_` and thus `maxExpandedBorrowLimit_` is 0 and `baseBorrowLimit_` can not be 0.

        uint256 temp_ = (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_EXPAND_PERCENT) & X14; // temp_ = extract borrow expand percent (is in 1e2 decimals)

        uint256 maxExpansionLimit_;
        uint256 maxExpandedBorrowLimit_;
        unchecked {
            // calculate max expansion limit: Max amount limit can expand to since last interaction
            // userBorrow_ needs to be atleast 1e73 to overflow max limit of ~1e77 in uint256 (no token in existence where this is possible).
            maxExpansionLimit_ = ((userBorrow_ * temp_) / FOUR_DECIMALS);

            // calculate max borrow limit: Max point limit can increase to since last interaction
            maxExpandedBorrowLimit_ = userBorrow_ + maxExpansionLimit_;
        }

        // currentBorrowLimit_ = extract base borrow limit
        currentBorrowLimit_ = (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_BASE_BORROW_LIMIT) & X18;
        currentBorrowLimit_ =
            (currentBorrowLimit_ >> DEFAULT_EXPONENT_SIZE) <<
            (currentBorrowLimit_ & DEFAULT_EXPONENT_MASK);

        if (maxExpandedBorrowLimit_ < currentBorrowLimit_) {
            return currentBorrowLimit_;
        }
        // time elapsed since last borrow limit was set (in seconds)
        unchecked {
            // temp_ = timeElapsed_ (last timestamp can not be > current timestamp)
            temp_ =
                block.timestamp -
                ((userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_LAST_UPDATE_TIMESTAMP) & X33); // extract last udpate timestamp
        }

        // currentBorrowLimit_ = expandedBorrowableAmount + extract last set borrow limit
        currentBorrowLimit_ =
            // calculate borrow limit expansion since last interaction for `expandPercent` that is elapsed of `expandDuration`.
            // divisor is extract expand duration (after this, full expansion to expandPercentage happened).
            ((maxExpansionLimit_ * temp_) /
                ((userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_EXPAND_DURATION) & X24)) + // expand duration can never be 0
            //  extract last set borrow limit
            BigMathMinified.fromBigNumber(
                (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_PREVIOUS_BORROW_LIMIT) & X64,
                DEFAULT_EXPONENT_SIZE,
                DEFAULT_EXPONENT_MASK
            );

        // if timeElapsed is bigger than expandDuration, new borrow limit would be > max expansion,
        // so set to `maxExpandedBorrowLimit_` in that case.
        // also covers the case where last process timestamp = 0 (timeElapsed would simply be very big)
        if (currentBorrowLimit_ > maxExpandedBorrowLimit_) {
            currentBorrowLimit_ = maxExpandedBorrowLimit_;
        }
        // temp_ = extract hard max borrow limit. Above this user can never borrow (not expandable above)
        temp_ = (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_MAX_BORROW_LIMIT) & X18;
        temp_ = (temp_ >> DEFAULT_EXPONENT_SIZE) << (temp_ & DEFAULT_EXPONENT_MASK);

        if (currentBorrowLimit_ > temp_) {
            currentBorrowLimit_ = temp_;
        }
    }

    /// @dev calculates borrow limit after an operate execution:
    /// total amount user borrow can reach (not borrowable amount in current operation).
    /// i.e. if user has borrowed 50M and can still borrow 5M, this method returns the total 55M, not the borrowable amount 5M
    /// @param userBorrowData_ user borrow data packed uint256 from storage
    /// @param userBorrow_ current user borrow amount already extracted from `userBorrowData_` and added / subtracted with the executed operate amount
    /// @param newBorrowLimit_ current borrow limit updated for expansion since last interaction, result from `calcBorrowLimitBeforeOperate`
    /// @return borrowLimit_ updated borrow limit that should be written to storage.
    ///                      returned value is in raw for with interest mode, normal amount for interest free mode!
    function calcBorrowLimitAfterOperate(
        uint256 userBorrowData_,
        uint256 userBorrow_,
        uint256 newBorrowLimit_
    ) internal pure returns (uint256 borrowLimit_) {
        // temp_ = extract borrow expand percent
        uint256 temp_ = (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_EXPAND_PERCENT) & X14; // (is in 1e2 decimals)

        unchecked {
            // borrowLimit_ = calculate maximum borrow limit at full expansion.
            // userBorrow_ needs to be atleast 1e73 to overflow max limit of ~1e77 in uint256 (no token in existence where this is possible).
            borrowLimit_ = userBorrow_ + ((userBorrow_ * temp_) / FOUR_DECIMALS);
        }

        // temp_ = extract base borrow limit
        temp_ = (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_BASE_BORROW_LIMIT) & X18;
        temp_ = (temp_ >> DEFAULT_EXPONENT_SIZE) << (temp_ & DEFAULT_EXPONENT_MASK);

        if (borrowLimit_ < temp_) {
            // below base limit, borrow limit is always base limit
            return temp_;
        }
        // temp_ = extract hard max borrow limit. Above this user can never borrow (not expandable above)
        temp_ = (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_MAX_BORROW_LIMIT) & X18;
        temp_ = (temp_ >> DEFAULT_EXPONENT_SIZE) << (temp_ & DEFAULT_EXPONENT_MASK);

        // make sure fully expanded borrow limit is not above hard max borrow limit
        if (borrowLimit_ > temp_) {
            borrowLimit_ = temp_;
        }
        // if new borrow limit is > max borrow limit, set max borrow limit.
        // (e.g. on a repay shrinking instantly to fully expanded borrow limit from new borrow amount. shrinking is instant)
        if (newBorrowLimit_ > borrowLimit_) {
            return borrowLimit_;
        }
        return newBorrowLimit_;
    }

    ///////////////////////////////////////////////////////////////////////////
    //////////                      CALC RATES                        /////////
    ///////////////////////////////////////////////////////////////////////////

    /// @dev Calculates new borrow rate from utilization for a token
    /// @param rateData_ rate data packed uint256 from storage for the token
    /// @param utilization_ totalBorrow / totalSupply. 1e4 = 100% utilization
    /// @return rate_ rate for that particular token in 1e2 precision (e.g. 5% rate = 500)
    function calcBorrowRateFromUtilization(uint256 rateData_, uint256 utilization_) internal returns (uint256 rate_) {
        // extract rate version: 4 bits (0xF) starting from bit 0
        uint256 rateVersion_ = (rateData_ & 0xF);

        if (rateVersion_ == 1) {
            rate_ = calcRateV1(rateData_, utilization_);
        } else if (rateVersion_ == 2) {
            rate_ = calcRateV2(rateData_, utilization_);
        } else {
            revert FluidLiquidityCalcsError(ErrorTypes.LiquidityCalcs__UnsupportedRateVersion);
        }

        if (rate_ > X16) {
            // hard cap for borrow rate at maximum value 16 bits (65535) to make sure it does not overflow storage space.
            // this is unlikely to ever happen if configs stay within expected levels.
            rate_ = X16;
            // emit event to more easily become aware
            emit BorrowRateMaxCap();
        }
    }

    /// @dev calculates the borrow rate based on utilization for rate data version 1 (with one kink) in 1e2 precision
    /// @param rateData_ rate data packed uint256 from storage for the token
    /// @param utilization_  in 1e2 (100% = 1e4)
    /// @return rate_ rate in 1e2 precision
    function calcRateV1(uint256 rateData_, uint256 utilization_) internal pure returns (uint256 rate_) {
        /// For rate v1 (one kink) ------------------------------------------------------
        /// Next 16  bits =>  4 - 19 => Rate at utilization 0% (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Next 16  bits =>  20- 35 => Utilization at kink1 (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Next 16  bits =>  36- 51 => Rate at utilization kink1 (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Next 16  bits =>  52- 67 => Rate at utilization 100% (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Last 188 bits =>  68-255 => blank, might come in use in future

        // y = mx + c.
        // y is borrow rate
        // x is utilization
        // m = slope (m can be 0 but never negative)
        // c is constant (c can be negative)

        uint256 y1_;
        uint256 y2_;
        uint256 x1_;
        uint256 x2_;

        // extract kink1: 16 bits (0xFFFF) starting from bit 20
        // kink is in 1e2, same as utilization, so no conversion needed for direct comparison of the two
        uint256 kink1_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V1_UTILIZATION_AT_KINK) & X16;
        if (utilization_ < kink1_) {
            // if utilization is less than kink
            y1_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_ZERO) & X16;
            y2_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_KINK) & X16;
            x1_ = 0; // 0%
            x2_ = kink1_;
        } else {
            // else utilization is greater than kink
            y1_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_KINK) & X16;
            y2_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_MAX) & X16;
            x1_ = kink1_;
            x2_ = FOUR_DECIMALS; // 100%
        }

        int256 constant_;
        uint256 slope_;
        unchecked {
            // calculating slope with twelve decimal precision. m = (y2 - y1) / (x2 - x1).
            // utilization of x2 can not be <= utilization of x1 (so no underflow or 0 divisor) and rate at y2 can not be < rate at y1
            // y is in 1e2 so can not overflow when multiplied with TWELVE_DECIMALS
            slope_ = ((y2_ - y1_) * TWELVE_DECIMALS) / (x2_ - x1_);

            // calculating constant at 12 decimal precision. slope is already in 12 decimal hence only multiple with y1. c = y - mx.
            // maximum y1_ value is 65535. 65535 * 1e12 can not overflow int256
            // maximum slope is 65535 - 0 * TWELVE_DECIMALS / 1 = 65535 * 1e12;
            // maximum x1_ is 100% (9_999 actually) => slope_ * x1_ can not overflow int256
            // subtraction most extreme case would be  0 - max value slope_ * x1_ => can not underflow int256
            constant_ = int256(y1_ * TWELVE_DECIMALS) - int256(slope_ * x1_);

            // calculating new borrow rate
            // - slope_ max value is 65535 * 1e12,
            // - utilization max value is let's say 500% (extreme case where borrow rate increases borrow amount without new supply)
            // - constant max value is 65535 * 1e12
            // so max values are 65535 * 1e12 * 50_000 + 65535 * 1e12 -> 3.2768*10^21, which easily fits int256
            // divisor TWELVE_DECIMALS can not be 0
            rate_ = (uint256(int256(slope_ * utilization_) + constant_)) / TWELVE_DECIMALS;
        }
    }

    /// @dev calculates the borrow rate based on utilization for rate data version 2 (with two kinks) in 1e4 precision
    /// @param rateData_ rate data packed uint256 from storage for the token
    /// @param utilization_  in 1e2 (100% = 1e4)
    /// @return rate_ rate in 1e4 precision
    function calcRateV2(uint256 rateData_, uint256 utilization_) internal pure returns (uint256 rate_) {
        /// For rate v2 (two kinks) -----------------------------------------------------
        /// Next 16  bits =>  4 - 19 => Rate at utilization 0% (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Next 16  bits =>  20- 35 => Utilization at kink1 (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Next 16  bits =>  36- 51 => Rate at utilization kink1 (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Next 16  bits =>  52- 67 => Utilization at kink2 (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Next 16  bits =>  68- 83 => Rate at utilization kink2 (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Next 16  bits =>  84- 99 => Rate at utilization 100% (in 1e2: 100% = 10_000; 1% = 100 -> max value 65535)
        /// Last 156 bits => 100-255 => blank, might come in use in future

        // y = mx + c.
        // y is borrow rate
        // x is utilization
        // m = slope (m can be 0 but never negative)
        // c is constant (c can be negative)

        uint256 y1_;
        uint256 y2_;
        uint256 x1_;
        uint256 x2_;

        // extract kink1: 16 bits (0xFFFF) starting from bit 20
        // kink is in 1e2, same as utilization, so no conversion needed for direct comparison of the two
        uint256 kink1_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V2_UTILIZATION_AT_KINK1) & X16;
        if (utilization_ < kink1_) {
            // if utilization is less than kink1
            y1_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_ZERO) & X16;
            y2_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_KINK1) & X16;
            x1_ = 0; // 0%
            x2_ = kink1_;
        } else {
            // extract kink2: 16 bits (0xFFFF) starting from bit 52
            uint256 kink2_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V2_UTILIZATION_AT_KINK2) & X16;
            if (utilization_ < kink2_) {
                // if utilization is less than kink2
                y1_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_KINK1) & X16;
                y2_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_KINK2) & X16;
                x1_ = kink1_;
                x2_ = kink2_;
            } else {
                // else utilization is greater than kink2
                y1_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_KINK2) & X16;
                y2_ = (rateData_ >> LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_MAX) & X16;
                x1_ = kink2_;
                x2_ = FOUR_DECIMALS;
            }
        }

        int256 constant_;
        uint256 slope_;
        unchecked {
            // calculating slope with twelve decimal precision. m = (y2 - y1) / (x2 - x1).
            // utilization of x2 can not be <= utilization of x1 (so no underflow or 0 divisor) and rate at y2 can not be < rate at y1
            // y is in 1e2 so can not overflow when multiplied with TWELVE_DECIMALS
            slope_ = ((y2_ - y1_) * TWELVE_DECIMALS) / (x2_ - x1_);

            // calculating constant at 12 decimal precision. slope is already in 12 decimal hence only multiple with y1. c = y - mx.
            // maximum y1_ value is 65535. 65535 * 1e12 can not overflow int256
            // maximum slope is 65535 - 0 * TWELVE_DECIMALS / 1 = 65535 * 1e12;
            // maximum x1_ is 100% (9_999 actually) => slope_ * x1_ can not overflow int256
            // subtraction most extreme case would be  0 - max value slope_ * x1_ => can not underflow int256
            constant_ = int256(y1_ * TWELVE_DECIMALS) - int256(slope_ * x1_);

            // calculating new borrow rate
            // - slope_ max value is 65535 * 1e12,
            // - utilization max value is let's say 500% (extreme case where borrow rate increases borrow amount without new supply)
            // - constant max value is 65535 * 1e12
            // so max values are 65535 * 1e12 * 50_000 + 65535 * 1e12 -> 3.2768*10^21, which easily fits int256
            // divisor TWELVE_DECIMALS can not be 0
            rate_ = (uint256(int256(slope_ * utilization_) + constant_)) / TWELVE_DECIMALS;
        }
    }

    /// @dev reads the total supply out of Liquidity packed storage `totalAmounts_` for `supplyExchangePrice_`
    function getTotalSupply(
        uint256 totalAmounts_,
        uint256 supplyExchangePrice_
    ) internal pure returns (uint256 totalSupply_) {
        // totalSupply_ => supplyInterestFree
        totalSupply_ = (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_SUPPLY_INTEREST_FREE) & X64;
        totalSupply_ = (totalSupply_ >> DEFAULT_EXPONENT_SIZE) << (totalSupply_ & DEFAULT_EXPONENT_MASK);

        uint256 totalSupplyRaw_ = totalAmounts_ & X64; // no shifting as supplyRaw is first 64 bits
        totalSupplyRaw_ = (totalSupplyRaw_ >> DEFAULT_EXPONENT_SIZE) << (totalSupplyRaw_ & DEFAULT_EXPONENT_MASK);

        // totalSupply = supplyInterestFree + supplyRawInterest normalized from raw
        totalSupply_ += ((totalSupplyRaw_ * supplyExchangePrice_) / EXCHANGE_PRICES_PRECISION);
    }

    /// @dev reads the total borrow out of Liquidity packed storage `totalAmounts_` for `borrowExchangePrice_`
    function getTotalBorrow(
        uint256 totalAmounts_,
        uint256 borrowExchangePrice_
    ) internal pure returns (uint256 totalBorrow_) {
        // totalBorrow_ => borrowInterestFree
        // no & mask needed for borrow interest free as it occupies the last bits in the storage slot
        totalBorrow_ = (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_BORROW_INTEREST_FREE);
        totalBorrow_ = (totalBorrow_ >> DEFAULT_EXPONENT_SIZE) << (totalBorrow_ & DEFAULT_EXPONENT_MASK);

        uint256 totalBorrowRaw_ = (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_BORROW_WITH_INTEREST) & X64;
        totalBorrowRaw_ = (totalBorrowRaw_ >> DEFAULT_EXPONENT_SIZE) << (totalBorrowRaw_ & DEFAULT_EXPONENT_MASK);

        // totalBorrow = borrowInterestFree + borrowRawInterest normalized from raw
        totalBorrow_ += ((totalBorrowRaw_ * borrowExchangePrice_) / EXCHANGE_PRICES_PRECISION);
    }
}
","keccak256":"0xbabe330c3520a37876b4572d8e262191615d5cfc44eac9d236d4580d148c3428","license":"BUSL-1.1"},"contracts/libraries/liquiditySlotsLink.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

/// @notice library that helps in reading / working with storage slot data of Fluid Liquidity.
/// @dev as all data for Fluid Liquidity is internal, any data must be fetched directly through manual
/// slot reading through this library or, if gas usage is less important, through the LiquidityResolver.
library LiquiditySlotsLink {
    /// @dev storage slot for status at Liquidity
    uint256 internal constant LIQUIDITY_STATUS_SLOT = 1;
    /// @dev storage slot for auths mapping at Liquidity
    uint256 internal constant LIQUIDITY_AUTHS_MAPPING_SLOT = 2;
    /// @dev storage slot for guardians mapping at Liquidity
    uint256 internal constant LIQUIDITY_GUARDIANS_MAPPING_SLOT = 3;
    /// @dev storage slot for user class mapping at Liquidity
    uint256 internal constant LIQUIDITY_USER_CLASS_MAPPING_SLOT = 4;
    /// @dev storage slot for exchangePricesAndConfig mapping at Liquidity
    uint256 internal constant LIQUIDITY_EXCHANGE_PRICES_MAPPING_SLOT = 5;
    /// @dev storage slot for rateData mapping at Liquidity
    uint256 internal constant LIQUIDITY_RATE_DATA_MAPPING_SLOT = 6;
    /// @dev storage slot for totalAmounts mapping at Liquidity
    uint256 internal constant LIQUIDITY_TOTAL_AMOUNTS_MAPPING_SLOT = 7;
    /// @dev storage slot for user supply double mapping at Liquidity
    uint256 internal constant LIQUIDITY_USER_SUPPLY_DOUBLE_MAPPING_SLOT = 8;
    /// @dev storage slot for user borrow double mapping at Liquidity
    uint256 internal constant LIQUIDITY_USER_BORROW_DOUBLE_MAPPING_SLOT = 9;

    // --------------------------------
    // @dev stacked uint256 storage slots bits position data for each:

    // ExchangePricesAndConfig
    uint256 internal constant BITS_EXCHANGE_PRICES_BORROW_RATE = 0;
    uint256 internal constant BITS_EXCHANGE_PRICES_FEE = 16;
    uint256 internal constant BITS_EXCHANGE_PRICES_UTILIZATION = 30;
    uint256 internal constant BITS_EXCHANGE_PRICES_UPDATE_THRESHOLD = 44;
    uint256 internal constant BITS_EXCHANGE_PRICES_LAST_TIMESTAMP = 58;
    uint256 internal constant BITS_EXCHANGE_PRICES_SUPPLY_EXCHANGE_PRICE = 91;
    uint256 internal constant BITS_EXCHANGE_PRICES_BORROW_EXCHANGE_PRICE = 155;
    uint256 internal constant BITS_EXCHANGE_PRICES_SUPPLY_RATIO = 219;
    uint256 internal constant BITS_EXCHANGE_PRICES_BORROW_RATIO = 234;

    // RateData:
    uint256 internal constant BITS_RATE_DATA_VERSION = 0;
    // RateData: V1
    uint256 internal constant BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_ZERO = 4;
    uint256 internal constant BITS_RATE_DATA_V1_UTILIZATION_AT_KINK = 20;
    uint256 internal constant BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_KINK = 36;
    uint256 internal constant BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_MAX = 52;
    // RateData: V2
    uint256 internal constant BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_ZERO = 4;
    uint256 internal constant BITS_RATE_DATA_V2_UTILIZATION_AT_KINK1 = 20;
    uint256 internal constant BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_KINK1 = 36;
    uint256 internal constant BITS_RATE_DATA_V2_UTILIZATION_AT_KINK2 = 52;
    uint256 internal constant BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_KINK2 = 68;
    uint256 internal constant BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_MAX = 84;

    // TotalAmounts
    uint256 internal constant BITS_TOTAL_AMOUNTS_SUPPLY_WITH_INTEREST = 0;
    uint256 internal constant BITS_TOTAL_AMOUNTS_SUPPLY_INTEREST_FREE = 64;
    uint256 internal constant BITS_TOTAL_AMOUNTS_BORROW_WITH_INTEREST = 128;
    uint256 internal constant BITS_TOTAL_AMOUNTS_BORROW_INTEREST_FREE = 192;

    // UserSupplyData
    uint256 internal constant BITS_USER_SUPPLY_MODE = 0;
    uint256 internal constant BITS_USER_SUPPLY_AMOUNT = 1;
    uint256 internal constant BITS_USER_SUPPLY_PREVIOUS_WITHDRAWAL_LIMIT = 65;
    uint256 internal constant BITS_USER_SUPPLY_LAST_UPDATE_TIMESTAMP = 129;
    uint256 internal constant BITS_USER_SUPPLY_EXPAND_PERCENT = 162;
    uint256 internal constant BITS_USER_SUPPLY_EXPAND_DURATION = 176;
    uint256 internal constant BITS_USER_SUPPLY_BASE_WITHDRAWAL_LIMIT = 200;
    uint256 internal constant BITS_USER_SUPPLY_IS_PAUSED = 255;

    // UserBorrowData
    uint256 internal constant BITS_USER_BORROW_MODE = 0;
    uint256 internal constant BITS_USER_BORROW_AMOUNT = 1;
    uint256 internal constant BITS_USER_BORROW_PREVIOUS_BORROW_LIMIT = 65;
    uint256 internal constant BITS_USER_BORROW_LAST_UPDATE_TIMESTAMP = 129;
    uint256 internal constant BITS_USER_BORROW_EXPAND_PERCENT = 162;
    uint256 internal constant BITS_USER_BORROW_EXPAND_DURATION = 176;
    uint256 internal constant BITS_USER_BORROW_BASE_BORROW_LIMIT = 200;
    uint256 internal constant BITS_USER_BORROW_MAX_BORROW_LIMIT = 218;
    uint256 internal constant BITS_USER_BORROW_IS_PAUSED = 255;

    // --------------------------------

    /// @notice Calculating the slot ID for Liquidity contract for single mapping at `slot_` for `key_`
    function calculateMappingStorageSlot(uint256 slot_, address key_) internal pure returns (bytes32) {
        return keccak256(abi.encode(key_, slot_));
    }

    /// @notice Calculating the slot ID for Liquidity contract for double mapping at `slot_` for `key1_` and `key2_`
    function calculateDoubleMappingStorageSlot(
        uint256 slot_,
        address key1_,
        address key2_
    ) internal pure returns (bytes32) {
        bytes32 intermediateSlot_ = keccak256(abi.encode(key1_, slot_));
        return keccak256(abi.encode(key2_, intermediateSlot_));
    }
}
","keccak256":"0x02e5a027002cb25e52a450c92cba19c2ab6bc33213a0343d4f57115c6d881030","license":"BUSL-1.1"},"contracts/liquidity/adminModule/events.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { Structs } from "./structs.sol";

contract Events is Structs {
    /// @notice emitted when allowed auths are updated
    event LogUpdateAuths(AddressBool[] authsStatus);

    /// @notice emitted when allowed guardians are updated
    event LogUpdateGuardians(AddressBool[] guardiansStatus);

    /// @notice emitted when revenue collector address is updated
    event LogUpdateRevenueCollector(address indexed revenueCollector);

    /// @notice emitted when status is changed (paused / unpaused)
    event LogChangeStatus(uint256 indexed newStatus);

    /// @notice emitted when user classes are updated
    event LogUpdateUserClasses(AddressUint256[] userClasses);

    /// @notice emitted when token configs are updated
    event LogUpdateTokenConfigs(TokenConfig[] tokenConfigs);

    /// @notice emitted when user supply configs are updated
    event LogUpdateUserSupplyConfigs(UserSupplyConfig[] userSupplyConfigs);

    /// @notice emitted when user borrow configs are updated
    event LogUpdateUserBorrowConfigs(UserBorrowConfig[] userBorrowConfigs);

    /// @notice emitted when a user gets certain tokens paused
    event LogPauseUser(address user, address[] supplyTokens, address[] borrowTokens);

    /// @notice emitted when a user gets certain tokens unpaused
    event LogUnpauseUser(address user, address[] supplyTokens, address[] borrowTokens);

    /// @notice emitted when token rate data is updated with rate data v1
    event LogUpdateRateDataV1s(RateDataV1Params[] tokenRateDatas);

    /// @notice emitted when token rate data is updated with rate data v2
    event LogUpdateRateDataV2s(RateDataV2Params[] tokenRateDatas);

    /// @notice emitted when revenue is collected
    event LogCollectRevenue(address indexed token, uint256 indexed amount);

    /// @notice emitted when exchange prices and borrow rate are updated
    event LogUpdateExchangePrices(
        address indexed token,
        uint256 indexed supplyExchangePrice,
        uint256 indexed borrowExchangePrice,
        uint256 borrowRate,
        uint256 utilization
    );
}
","keccak256":"0xaaab5be04a1832e8d95c46ce041ae85db1ff71090e2cfc72c1d675af3943bdec","license":"BUSL-1.1"},"contracts/liquidity/adminModule/main.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Address } from "@openzeppelin/contracts/utils/Address.sol";
import { FixedPointMathLib } from "solmate/src/utils/FixedPointMathLib.sol";

import { BigMathMinified } from "../../libraries/bigMathMinified.sol";
import { LiquidityCalcs } from "../../libraries/liquidityCalcs.sol";
import { LiquiditySlotsLink } from "../../libraries/liquiditySlotsLink.sol";
import { Events } from "./events.sol";
import { Structs } from "./structs.sol";
import { CommonHelpers } from "../common/helpers.sol";
import { ILiquidityAdmin } from "../interfaces/iLiquidity.sol";
import { ErrorTypes } from "../errorTypes.sol";
import { Error } from "../error.sol";

abstract contract AdminModuleConstants is Error {
    /// @dev hard cap value for max borrow limit, used as sanity check. Usually 10x of total supply.
    uint256 public immutable NATIVE_TOKEN_MAX_BORROW_LIMIT_CAP;

    constructor(uint256 nativeTokenMaxBorrowLimitCap_) {
        if (nativeTokenMaxBorrowLimitCap_ == 0) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidParams);
        }

        NATIVE_TOKEN_MAX_BORROW_LIMIT_CAP = nativeTokenMaxBorrowLimitCap_;
    }
}

/// @notice Fluid Liquidity Governance only related methods
abstract contract GovernanceModule is ILiquidityAdmin, CommonHelpers, Events, AdminModuleConstants {
    /// @notice only governance guard
    modifier onlyGovernance() {
        if (_getGovernanceAddr() != msg.sender) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__OnlyGovernance);
        }
        _;
    }

    /// @dev checks that `value_` is a valid address (not zero address)
    function _checkValidAddress(address value_) internal pure {
        if (value_ == address(0)) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__AddressZero);
        }
    }

    /// @dev checks that `value_` address is a contract (which includes address zero check)
    function _checkIsContractOrNativeAddress(address value_) internal view {
        if (value_.code.length == 0 && value_ != NATIVE_TOKEN_ADDRESS) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__AddressNotAContract);
        }
    }

    /// @inheritdoc ILiquidityAdmin
    function updateAuths(AddressBool[] calldata authsStatus_) external onlyGovernance {
        uint256 length_ = authsStatus_.length;
        for (uint256 i; i < length_; ) {
            _checkValidAddress(authsStatus_[i].addr);

            _isAuth[authsStatus_[i].addr] = authsStatus_[i].value ? 1 : 0;

            unchecked {
                ++i;
            }
        }

        emit LogUpdateAuths(authsStatus_);
    }

    /// @inheritdoc ILiquidityAdmin
    function updateGuardians(AddressBool[] calldata guardiansStatus_) external onlyGovernance {
        uint256 length_ = guardiansStatus_.length;
        for (uint256 i; i < length_; ) {
            _checkValidAddress(guardiansStatus_[i].addr);

            _isGuardian[guardiansStatus_[i].addr] = guardiansStatus_[i].value ? 1 : 0;

            unchecked {
                ++i;
            }
        }

        emit LogUpdateGuardians(guardiansStatus_);
    }

    /// @inheritdoc ILiquidityAdmin
    function updateRevenueCollector(address revenueCollector_) external onlyGovernance {
        _checkValidAddress(revenueCollector_);

        _revenueCollector = revenueCollector_;

        emit LogUpdateRevenueCollector(revenueCollector_);
    }
}

abstract contract AuthInternals is Error, CommonHelpers, Events {
    /// @dev computes rata data packed uint256 for version 1 rate input params telling desired values
    /// at different uzilitation points (0%, kink, 100%)
    /// @param rataDataV1Params_ rata data params for a given token
    /// @return rateData_ packed uint256 rate data
    function _computeRateDataPackedV1(
        RateDataV1Params memory rataDataV1Params_
    ) internal pure returns (uint256 rateData_) {
        if (rataDataV1Params_.rateAtUtilizationZero > X16) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__RATE_AT_UTIL_ZERO);
        }
        if (rataDataV1Params_.rateAtUtilizationKink > X16) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__RATE_AT_UTIL_KINK);
        }
        if (rataDataV1Params_.rateAtUtilizationMax > X16) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__RATE_AT_UTIL_MAX);
        }
        if (
            // kink must not be 0 or >= 100% (being 0 or 100% would lead to division through 0 at calculation time)
            rataDataV1Params_.kink == 0 ||
            rataDataV1Params_.kink >= FOUR_DECIMALS ||
            // borrow rate must be increasing as utilization grows
            rataDataV1Params_.rateAtUtilizationZero > rataDataV1Params_.rateAtUtilizationKink ||
            rataDataV1Params_.rateAtUtilizationKink > rataDataV1Params_.rateAtUtilizationMax
        ) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidParams);
        }

        rateData_ =
            1 | // version
            (rataDataV1Params_.rateAtUtilizationZero << LiquiditySlotsLink.BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_ZERO) |
            (rataDataV1Params_.kink << LiquiditySlotsLink.BITS_RATE_DATA_V1_UTILIZATION_AT_KINK) |
            (rataDataV1Params_.rateAtUtilizationKink << LiquiditySlotsLink.BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_KINK) |
            (rataDataV1Params_.rateAtUtilizationMax << LiquiditySlotsLink.BITS_RATE_DATA_V1_RATE_AT_UTILIZATION_MAX);
    }

    /// @dev computes rata data packed uint256 for rate version 2 input params telling desired values
    /// at different uzilitation points (0%, kink1, kink2, 100%)
    /// @param rataDataV2Params_ rata data params for a given token
    /// @return rateData_ packed uint256 rate data
    function _computeRateDataPackedV2(
        RateDataV2Params memory rataDataV2Params_
    ) internal pure returns (uint256 rateData_) {
        if (rataDataV2Params_.rateAtUtilizationZero > X16) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__RATE_AT_UTIL_ZERO);
        }
        if (rataDataV2Params_.rateAtUtilizationKink1 > X16) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__RATE_AT_UTIL_KINK1);
        }
        if (rataDataV2Params_.rateAtUtilizationKink2 > X16) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__RATE_AT_UTIL_KINK2);
        }
        if (rataDataV2Params_.rateAtUtilizationMax > X16) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__RATE_AT_UTIL_MAX_V2);
        }
        if (
            // kink can not be 0, >= 100% or >= kink2 (would lead to division through 0 at calculation time)
            rataDataV2Params_.kink1 == 0 ||
            rataDataV2Params_.kink1 >= FOUR_DECIMALS ||
            rataDataV2Params_.kink1 >= rataDataV2Params_.kink2 ||
            // kink2 can not be >= 100% (must be > kink1 already checked)
            rataDataV2Params_.kink2 >= FOUR_DECIMALS ||
            // borrow rate must be increasing as utilization grows
            rataDataV2Params_.rateAtUtilizationZero > rataDataV2Params_.rateAtUtilizationKink1 ||
            rataDataV2Params_.rateAtUtilizationKink1 > rataDataV2Params_.rateAtUtilizationKink2 ||
            rataDataV2Params_.rateAtUtilizationKink2 > rataDataV2Params_.rateAtUtilizationMax
        ) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidParams);
        }

        rateData_ =
            2 | // version
            (rataDataV2Params_.rateAtUtilizationZero << LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_ZERO) |
            (rataDataV2Params_.kink1 << LiquiditySlotsLink.BITS_RATE_DATA_V2_UTILIZATION_AT_KINK1) |
            (rataDataV2Params_.rateAtUtilizationKink1 <<
                LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_KINK1) |
            (rataDataV2Params_.kink2 << LiquiditySlotsLink.BITS_RATE_DATA_V2_UTILIZATION_AT_KINK2) |
            (rataDataV2Params_.rateAtUtilizationKink2 <<
                LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_KINK2) |
            (rataDataV2Params_.rateAtUtilizationMax << LiquiditySlotsLink.BITS_RATE_DATA_V2_RATE_AT_UTILIZATION_MAX);
    }

    /// @dev updates the exchange prices in storage for `token_` and returns `supplyExchangePrice_` and `borrowExchangePrice_`.
    /// Recommended to use only in a method that later calls `_updateExchangePricesAndRates()`.
    function _updateExchangePrices(
        address token_
    ) internal returns (uint256 supplyExchangePrice_, uint256 borrowExchangePrice_) {
        uint256 exchangePricesAndConfig_ = _exchangePricesAndConfig[token_];

        // calculate the new exchange prices based on earned interest
        (supplyExchangePrice_, borrowExchangePrice_) = LiquidityCalcs.calcExchangePrices(exchangePricesAndConfig_);

        // ensure values written to storage do not exceed the dedicated bit space in packed uint256 slots
        if (supplyExchangePrice_ > X64 || borrowExchangePrice_ > X64) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__EXCHANGE_PRICES);
        }

        // write updated exchangePrices_ for token to storage
        _exchangePricesAndConfig[token_] =
            (exchangePricesAndConfig_ &
                // mask to update bits: 58-218 (timestamp and exchange prices)
                0xfffffffff80000000000000000000000000000000000000003ffffffffffffff) |
            (block.timestamp << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_LAST_TIMESTAMP) |
            (supplyExchangePrice_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_SUPPLY_EXCHANGE_PRICE) |
            (borrowExchangePrice_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_BORROW_EXCHANGE_PRICE);

        emit LogUpdateExchangePrices(
            token_,
            supplyExchangePrice_,
            borrowExchangePrice_,
            exchangePricesAndConfig_ & X16, // borrow rate is unchanged -> read from exchangePricesAndConfig_
            (exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_UTILIZATION) & X14 // utilization is unchanged -> read from exchangePricesAndConfig_
        );

        return (supplyExchangePrice_, borrowExchangePrice_);
    }

    /// @dev updates the exchange prices + rates in storage for `token_` and returns `supplyExchangePrice_` and `borrowExchangePrice_`
    function _updateExchangePricesAndRates(
        address token_
    ) internal returns (uint256 supplyExchangePrice_, uint256 borrowExchangePrice_) {
        uint256 exchangePricesAndConfig_ = _exchangePricesAndConfig[token_];
        // calculate the new exchange prices based on earned interest
        (supplyExchangePrice_, borrowExchangePrice_) = LiquidityCalcs.calcExchangePrices(exchangePricesAndConfig_);

        uint256 totalAmounts_ = _totalAmounts[token_];

        // calculate updated ratios
        // set supplyRatio_ = supplyWithInterest here, using that value for total supply before finish calc supplyRatio
        uint256 supplyRatio_ = ((BigMathMinified.fromBigNumber(
            (totalAmounts_ & X64),
            DEFAULT_EXPONENT_SIZE,
            DEFAULT_EXPONENT_MASK
        ) * supplyExchangePrice_) / EXCHANGE_PRICES_PRECISION);
        // set borrowRatio_ = borrowWithInterest here, using that value for total borrow before finish calc borrowRatio
        uint256 borrowRatio_ = ((BigMathMinified.fromBigNumber(
            (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_BORROW_WITH_INTEREST) & X64,
            DEFAULT_EXPONENT_SIZE,
            DEFAULT_EXPONENT_MASK
        ) * borrowExchangePrice_) / EXCHANGE_PRICES_PRECISION);

        uint256 supplyInterestFree_ = BigMathMinified.fromBigNumber(
            (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_SUPPLY_INTEREST_FREE) & X64,
            DEFAULT_EXPONENT_SIZE,
            DEFAULT_EXPONENT_MASK
        );

        uint256 borrowInterestFree_ = BigMathMinified.fromBigNumber(
            // no & mask needed for borrow interest free as it occupies the last bits in the storage slot
            (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_BORROW_INTEREST_FREE),
            DEFAULT_EXPONENT_SIZE,
            DEFAULT_EXPONENT_MASK
        );

        // calculate utilization: totalBorrow / totalSupply. If no supply, utilization must be 0 (avoid division by 0)
        uint256 utilization_ = 0;
        if (supplyRatio_ > 0 || supplyInterestFree_ > 0) {
            utilization_ = (((borrowRatio_ + borrowInterestFree_) * FOUR_DECIMALS) /
                (supplyRatio_ + supplyInterestFree_));
        }

        // finish calculating supply & borrow ratio
        // ########## calculating supply ratio ##########
        // supplyRatio_ holds value of supplyWithInterest below
        if (supplyRatio_ > supplyInterestFree_) {
            // supplyRatio_ is ratio with 1 bit as 0 as supply interest raw is bigger
            supplyRatio_ = ((supplyInterestFree_ * FOUR_DECIMALS) / supplyRatio_) << 1;
            // because of checking to divide by bigger amount, ratio can never be > 100%
        } else if (supplyRatio_ < supplyInterestFree_) {
            // supplyRatio_ is ratio with 1 bit as 1 as supply interest free is bigger
            supplyRatio_ = (((supplyRatio_ * FOUR_DECIMALS) / supplyInterestFree_) << 1) | 1;
            // because of checking to divide by bigger amount, ratio can never be > 100%
        } else {
            // supplies match exactly (supplyWithInterest  == supplyInterestFree)
            if (supplyRatio_ > 0) {
                // supplies are not 0 -> set ratio to 1 (with first bit set to 0, doesn't matter)
                supplyRatio_ = FOUR_DECIMALS << 1;
            } else {
                // if total supply = 0
                supplyRatio_ = 0;
            }
        }

        // ########## calculating borrow ratio ##########
        // borrowRatio_ holds value of borrowWithInterest below
        if (borrowRatio_ > borrowInterestFree_) {
            // borrowRatio_ is ratio with 1 bit as 0 as borrow interest raw is bigger
            borrowRatio_ = ((borrowInterestFree_ * FOUR_DECIMALS) / borrowRatio_) << 1;
            // because of checking to divide by bigger amount, ratio can never be > 100%
        } else if (borrowRatio_ < borrowInterestFree_) {
            // borrowRatio_ is ratio with 1 bit as 1 as borrow interest free is bigger
            borrowRatio_ = (((borrowRatio_ * FOUR_DECIMALS) / borrowInterestFree_) << 1) | 1;
            // because of checking to divide by bigger amount, ratio can never be > 100%
        } else {
            // borrows match exactly (borrowWithInterest  == borrowInterestFree)
            if (borrowRatio_ > 0) {
                // borrows are not 0 -> set ratio to 1 (with first bit set to 0, doesn't matter)
                borrowRatio_ = FOUR_DECIMALS << 1;
            } else {
                // if total borrows = 0
                borrowRatio_ = 0;
            }
        }

        // updated borrow rate from utilization
        uint256 borrowRate_ = LiquidityCalcs.calcBorrowRateFromUtilization(_rateData[token_], utilization_);

        // ensure values written to storage do not exceed the dedicated bit space in packed uint256 slots
        if (supplyExchangePrice_ > X64 || borrowExchangePrice_ > X64) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__EXCHANGE_PRICES);
        }
        if (utilization_ > X14) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__UTILIZATION);
        }

        // write updated exchangePrices_ for token to storage
        _exchangePricesAndConfig[token_] =
            (exchangePricesAndConfig_ &
                // mask to update bits: 0-15 (borrow rate), 30-43 (utilization), 58-248 (timestamp, exchange prices, ratios)
                0xfe000000000000000000000000000000000000000000000003fff0003fff0000) |
            borrowRate_ | // already includes an overflow check in `calcBorrowRateFromUtilization`
            (utilization_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_UTILIZATION) |
            (block.timestamp << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_LAST_TIMESTAMP) |
            (supplyExchangePrice_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_SUPPLY_EXCHANGE_PRICE) |
            (borrowExchangePrice_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_BORROW_EXCHANGE_PRICE) |
            // ratios can never be > 100%, no overflow check needed
            (supplyRatio_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_SUPPLY_RATIO) |
            (borrowRatio_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_BORROW_RATIO);

        emit LogUpdateExchangePrices(token_, supplyExchangePrice_, borrowExchangePrice_, borrowRate_, utilization_);

        return (supplyExchangePrice_, borrowExchangePrice_);
    }
}

/// @notice Fluid Liquidity Auths only related methods
abstract contract AuthModule is AuthInternals, GovernanceModule {
    using BigMathMinified for uint256;

    /// @dev max update on storage threshold as a sanity check. threshold is in 1e2, so 500 = 5%.
    /// A higher threshold is not allowed as it would cause the borrow rate to be updated too rarely.
    uint256 private constant MAX_TOKEN_CONFIG_UPDATE_THRESHOLD = 500;

    /// @dev only auths guard
    modifier onlyAuths() {
        if (_isAuth[msg.sender] & 1 != 1 && _getGovernanceAddr() != msg.sender) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__OnlyAuths);
        }
        _;
    }

    /// @inheritdoc ILiquidityAdmin
    function changeStatus(uint256 newStatus_) external onlyAuths {
        if (newStatus_ == 0 || newStatus_ > 2) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidParams);
        }

        _status = newStatus_;

        emit LogChangeStatus(newStatus_);
    }

    /// @inheritdoc ILiquidityAdmin
    function updateRateDataV1s(RateDataV1Params[] calldata tokensRateData_) external onlyAuths {
        uint256 length_ = tokensRateData_.length;
        uint256 rateData_;

        for (uint256 i; i < length_; ) {
            _checkIsContractOrNativeAddress(tokensRateData_[i].token);

            rateData_ = _rateData[tokensRateData_[i].token];

            // apply current rate data to exchange prices before updating to new rate data
            if (rateData_ != 0) {
                _updateExchangePrices(tokensRateData_[i].token);
            }

            _rateData[tokensRateData_[i].token] = _computeRateDataPackedV1(tokensRateData_[i]);

            if (rateData_ != 0) {
                // apply new rate data to borrow rate
                _updateExchangePricesAndRates(tokensRateData_[i].token);
            }

            unchecked {
                ++i;
            }
        }

        emit LogUpdateRateDataV1s(tokensRateData_);
    }

    /// @inheritdoc ILiquidityAdmin
    function updateRateDataV2s(RateDataV2Params[] calldata tokensRateData_) external onlyAuths {
        uint256 length_ = tokensRateData_.length;
        uint256 rateData_;

        for (uint256 i; i < length_; ) {
            _checkIsContractOrNativeAddress(tokensRateData_[i].token);

            rateData_ = _rateData[tokensRateData_[i].token];

            // apply current rate data to exchange prices before updating to new rate data
            if (rateData_ != 0) {
                _updateExchangePrices(tokensRateData_[i].token);
            }

            _rateData[tokensRateData_[i].token] = _computeRateDataPackedV2(tokensRateData_[i]);

            if (rateData_ != 0) {
                // apply new rate data to borrow rate
                _updateExchangePricesAndRates(tokensRateData_[i].token);
            }

            unchecked {
                ++i;
            }
        }

        emit LogUpdateRateDataV2s(tokensRateData_);
    }

    /// @inheritdoc ILiquidityAdmin
    function updateTokenConfigs(TokenConfig[] calldata tokenConfigs_) external onlyAuths {
        uint256 length_ = tokenConfigs_.length;
        uint256 exchangePricesAndConfig_;
        uint256 supplyExchangePrice_;
        uint256 borrowExchangePrice_;

        for (uint256 i; i < length_; ) {
            _checkIsContractOrNativeAddress(tokenConfigs_[i].token);
            if (_rateData[tokenConfigs_[i].token] == 0) {
                // rate data must be configured before token config
                revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidConfigOrder);
            }
            if (tokenConfigs_[i].fee > FOUR_DECIMALS) {
                // fee can not be > 100%
                revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__FEE);
            }
            if (tokenConfigs_[i].threshold > MAX_TOKEN_CONFIG_UPDATE_THRESHOLD) {
                // update on storage threshold can not be > MAX_TOKEN_CONFIG_UPDATE_THRESHOLD
                revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__THRESHOLD);
            }

            exchangePricesAndConfig_ = _exchangePricesAndConfig[tokenConfigs_[i].token];

            // extract exchange prices
            supplyExchangePrice_ =
                (exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_SUPPLY_EXCHANGE_PRICE) &
                X64;
            borrowExchangePrice_ =
                (exchangePricesAndConfig_ >> LiquiditySlotsLink.BITS_EXCHANGE_PRICES_BORROW_EXCHANGE_PRICE) &
                X64;

            if (supplyExchangePrice_ > 0 && borrowExchangePrice_ > 0) {
                // calculate the current exchange prices based on earned interest before updating fee + timestamp in storage
                (supplyExchangePrice_, borrowExchangePrice_) = LiquidityCalcs.calcExchangePrices(
                    exchangePricesAndConfig_
                );

                // ensure values written to storage do not exceed the dedicated bit space in packed uint256 slots
                if (supplyExchangePrice_ > X64 || borrowExchangePrice_ > X64) {
                    revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__EXCHANGE_PRICES);
                }
            } else {
                // exchange prices can only increase once set so if either one is 0, the other must be 0 too
                supplyExchangePrice_ = EXCHANGE_PRICES_PRECISION;
                borrowExchangePrice_ = EXCHANGE_PRICES_PRECISION;
            }

            _exchangePricesAndConfig[tokenConfigs_[i].token] =
                // mask to set bits 16-29 (fee), 44-218 (update storage threshold, timestamp, exchange prices)
                (exchangePricesAndConfig_ & 0xfffffffff80000000000000000000000000000000000000000000fffc000ffff) |
                (tokenConfigs_[i].fee << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_FEE) |
                (tokenConfigs_[i].threshold << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_UPDATE_THRESHOLD) |
                (block.timestamp << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_LAST_TIMESTAMP) |
                (supplyExchangePrice_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_SUPPLY_EXCHANGE_PRICE) |
                (borrowExchangePrice_ << LiquiditySlotsLink.BITS_EXCHANGE_PRICES_BORROW_EXCHANGE_PRICE);

            unchecked {
                ++i;
            }
        }

        emit LogUpdateTokenConfigs(tokenConfigs_);
    }

    /// @inheritdoc ILiquidityAdmin
    function updateUserClasses(AddressUint256[] calldata userClasses_) external onlyAuths {
        uint256 length_ = userClasses_.length;
        for (uint256 i = 0; i < length_; ) {
            if (userClasses_[i].value > 1) {
                revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidParams);
            }
            _checkIsContractOrNativeAddress(userClasses_[i].addr);

            _userClass[userClasses_[i].addr] = userClasses_[i].value;

            unchecked {
                ++i;
            }
        }

        emit LogUpdateUserClasses(userClasses_);
    }

    /// @inheritdoc ILiquidityAdmin
    function updateUserSupplyConfigs(UserSupplyConfig[] memory userSupplyConfigs_) external onlyAuths {
        uint256 userSupplyData_;
        uint256 totalAmounts_;
        uint256 totalSupplyRawInterest_;
        uint256 totalSupplyInterestFree_;
        uint256 supplyConversion_;
        uint256 withdrawLimitConversion_;
        uint256 supplyExchangePrice_;

        for (uint256 i; i < userSupplyConfigs_.length; ) {
            _checkIsContractOrNativeAddress(userSupplyConfigs_[i].user);
            _checkIsContractOrNativeAddress(userSupplyConfigs_[i].token);
            if (_exchangePricesAndConfig[userSupplyConfigs_[i].token] == 0) {
                // token config must be configured before setting any user supply config
                revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidConfigOrder);
            }
            if (
                userSupplyConfigs_[i].mode > 1 ||
                // can not set expand duration to 0 as that could cause a division by 0 in LiquidityCalcs.
                // having expand duration as 0 is anyway not an expected config so removing the possibility for that.
                // if no expansion is wanted, simply set expandDuration to 1 and expandPercent to 0.
                userSupplyConfigs_[i].expandDuration == 0
            ) {
                revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidParams);
            }
            if (userSupplyConfigs_[i].expandPercent > FOUR_DECIMALS) {
                revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__EXPAND_PERCENT);
            }
            if (userSupplyConfigs_[i].expandDuration > X24) {
                // duration is max 24 bits
                revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__EXPAND_DURATION);
            }
            if (userSupplyConfigs_[i].baseWithdrawalLimit == 0) {
                // base withdrawal limit can not be 0. As a side effect, this ensures that there is no supply config
                // where all values would be 0, so configured users can be differentiated in the mapping.
                revert FluidLiquidityError(ErrorTypes.AdminModule__LimitZero);
            }
            // @dev baseWithdrawalLimit has no max bits amount as it is in normal token amount & converted to BigNumber

            // convert base withdrawal limit to BigNumber for storage (10 | 8)
            userSupplyConfigs_[i].baseWithdrawalLimit = userSupplyConfigs_[i].baseWithdrawalLimit.toBigNumber(
                SMALL_COEFFICIENT_SIZE,
                DEFAULT_EXPONENT_SIZE,
                BigMathMinified.ROUND_DOWN // below baseWithdrawalLimit, 100% can be withdrawn
            );

            // get current user config data from storage
            userSupplyData_ = _userSupplyData[userSupplyConfigs_[i].user][userSupplyConfigs_[i].token];

            // if userSupplyData_ == 0 (new setup) or if mode is unchanged, normal update is possible.
            // else if mode changes, values have to be converted from raw <> normal etc.
            if (
                userSupplyData_ == 0 ||
                (userSupplyData_ & 1 == 0 && userSupplyConfigs_[i].mode == 0) ||
                (userSupplyData_ & 1 == 1 && userSupplyConfigs_[i].mode == 1)
            ) {
                // Updating user data on storage

                _userSupplyData[userSupplyConfigs_[i].user][userSupplyConfigs_[i].token] =
                    // mask to update first bit + bits 162-217 (expand percentage, expand duration, base limit)
                    (userSupplyData_ & 0xfffffffffc00000000000003fffffffffffffffffffffffffffffffffffffffe) |
                    (userSupplyConfigs_[i].mode) | // at first bit
                    (userSupplyConfigs_[i].expandPercent << LiquiditySlotsLink.BITS_USER_SUPPLY_EXPAND_PERCENT) |
                    (userSupplyConfigs_[i].expandDuration << LiquiditySlotsLink.BITS_USER_SUPPLY_EXPAND_DURATION) |
                    (userSupplyConfigs_[i].baseWithdrawalLimit <<
                        LiquiditySlotsLink.BITS_USER_SUPPLY_BASE_WITHDRAWAL_LIMIT);
            } else {
                // mode changes -> values have to be converted from raw <> normal etc.

                // if the mode changes then update _exchangePricesAndConfig related data in storage always
                // update exchange prices timely before applying changes that affect utilization, rate etc.
                _updateExchangePrices(userSupplyConfigs_[i].token);

                // get updated exchange prices for the token
                (supplyExchangePrice_, ) = LiquidityCalcs.calcExchangePrices(
                    _exchangePricesAndConfig[userSupplyConfigs_[i].token]
                );

                totalAmounts_ = _totalAmounts[userSupplyConfigs_[i].token];
                totalSupplyRawInterest_ = BigMathMinified.fromBigNumber(
                    (totalAmounts_ & X64),
                    DEFAULT_EXPONENT_SIZE,
                    DEFAULT_EXPONENT_MASK
                );
                totalSupplyInterestFree_ = BigMathMinified.fromBigNumber(
                    (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_SUPPLY_INTEREST_FREE) & X64,
                    DEFAULT_EXPONENT_SIZE,
                    DEFAULT_EXPONENT_MASK
                );

                // read current user supply & withdraw limit values
                // here supplyConversion_ = user supply amount
                supplyConversion_ = (userSupplyData_ >> LiquiditySlotsLink.BITS_USER_SUPPLY_AMOUNT) & X64; 
                supplyConversion_ =
                    (supplyConversion_ >> DEFAULT_EXPONENT_SIZE) <<
                    (supplyConversion_ & DEFAULT_EXPONENT_MASK);

                withdrawLimitConversion_ =
                    (userSupplyData_ >> LiquiditySlotsLink.BITS_USER_SUPPLY_PREVIOUS_WITHDRAWAL_LIMIT) &
                    X64; // here withdrawLimitConversion_ = previous user withdraw limit
                withdrawLimitConversion_ =
                    (withdrawLimitConversion_ >> DEFAULT_EXPONENT_SIZE) <<
                    (withdrawLimitConversion_ & DEFAULT_EXPONENT_MASK);

                // conversion of balance and limit according to the mode change
                if (userSupplyData_ & 1 == 0 && userSupplyConfigs_[i].mode == 1) {
                    // Changing balance from interest free to with interest -> normal amounts to raw amounts
                    // -> must divide by exchange price.

                    // decreasing interest free total supply
                    totalSupplyInterestFree_ = totalSupplyInterestFree_ > supplyConversion_
                        ? totalSupplyInterestFree_ - supplyConversion_
                        : 0;

                    supplyConversion_ = (supplyConversion_ * EXCHANGE_PRICES_PRECISION) / supplyExchangePrice_;
                    withdrawLimitConversion_ =
                        (withdrawLimitConversion_ * EXCHANGE_PRICES_PRECISION) /
                        supplyExchangePrice_;

                    // increasing raw (with interest) total supply
                    totalSupplyRawInterest_ += supplyConversion_;
                } else if (userSupplyData_ & 1 == 1 && userSupplyConfigs_[i].mode == 0) {
                    // Changing balance from with interest to interest free-> raw amounts to normal amounts
                    // -> must multiply by exchange price.

                    // decreasing raw (with interest) supply
                    totalSupplyRawInterest_ = totalSupplyRawInterest_ > supplyConversion_
                        ? totalSupplyRawInterest_ - supplyConversion_
                        : 0;

                    supplyConversion_ = (supplyConversion_ * supplyExchangePrice_) / EXCHANGE_PRICES_PRECISION;
                    withdrawLimitConversion_ =
                        (withdrawLimitConversion_ * supplyExchangePrice_) /
                        EXCHANGE_PRICES_PRECISION;

                    // increasing interest free total supply
                    totalSupplyInterestFree_ += supplyConversion_;
                }

                // change new converted amounts to BigNumber for storage
                supplyConversion_ = supplyConversion_.toBigNumber(
                    DEFAULT_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_DOWN
                );
                withdrawLimitConversion_ = withdrawLimitConversion_.toBigNumber(
                    DEFAULT_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_DOWN // withdrawal limit stores the amount that must stay supplied after withdrawal
                );

                // Updating user data on storage
                _userSupplyData[userSupplyConfigs_[i].user][userSupplyConfigs_[i].token] =
                    // mask to set bits 0-128 and 162-217 (all except last process timestamp)
                    (userSupplyData_ & 0xfffffffffc00000000000003fffffffe00000000000000000000000000000000) |
                    (userSupplyConfigs_[i].mode) |
                    (supplyConversion_ << LiquiditySlotsLink.BITS_USER_SUPPLY_AMOUNT) | // BigNumber converted can not overflow
                    (withdrawLimitConversion_ << LiquiditySlotsLink.BITS_USER_SUPPLY_PREVIOUS_WITHDRAWAL_LIMIT) | // BigNumber converted can not overflow
                    (userSupplyConfigs_[i].expandPercent << LiquiditySlotsLink.BITS_USER_SUPPLY_EXPAND_PERCENT) |
                    (userSupplyConfigs_[i].expandDuration << LiquiditySlotsLink.BITS_USER_SUPPLY_EXPAND_DURATION) |
                    (userSupplyConfigs_[i].baseWithdrawalLimit <<
                        LiquiditySlotsLink.BITS_USER_SUPPLY_BASE_WITHDRAWAL_LIMIT);

                // change new total amounts to BigNumber for storage
                totalSupplyRawInterest_ = totalSupplyRawInterest_.toBigNumber(
                    DEFAULT_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_DOWN
                );
                totalSupplyInterestFree_ = totalSupplyInterestFree_.toBigNumber(
                    DEFAULT_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_DOWN
                );

                // Updating total supplies on storage
                _totalAmounts[userSupplyConfigs_[i].token] =
                    // mask to set bits 0-127
                    (totalAmounts_ & 0xffffffffffffffffffffffffffffffff00000000000000000000000000000000) |
                    (totalSupplyRawInterest_) | // BigNumber converted can not overflow
                    (totalSupplyInterestFree_ << LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_SUPPLY_INTEREST_FREE); // BigNumber converted can not overflow

                // trigger update borrow rate, utilization, ratios etc.
                _updateExchangePricesAndRates(userSupplyConfigs_[i].token);
            }

            unchecked {
                ++i;
            }
        }

        emit LogUpdateUserSupplyConfigs(userSupplyConfigs_);
    }

    /// @inheritdoc ILiquidityAdmin
    function updateUserBorrowConfigs(UserBorrowConfig[] memory userBorrowConfigs_) external onlyAuths {
        uint256 userBorrowData_;
        uint256 totalAmounts_;
        uint256 totalBorrowRawInterest_;
        uint256 totalBorrowInterestFree_;
        uint256 borrowingConversion_;
        uint256 debtCeilingConversion_;
        uint256 borrowExchangePrice_;

        for (uint256 i; i < userBorrowConfigs_.length; ) {
            _checkIsContractOrNativeAddress(userBorrowConfigs_[i].user);
            _checkIsContractOrNativeAddress(userBorrowConfigs_[i].token);
            if (_exchangePricesAndConfig[userBorrowConfigs_[i].token] == 0) {
                // token config must be configured before setting any user borrow config
                revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidConfigOrder);
            }
            if (
                userBorrowConfigs_[i].mode > 1 ||
                // max debt ceiling must not be smaller than base debt ceiling. Also covers case where max = 0 but base > 0
                userBorrowConfigs_[i].baseDebtCeiling > userBorrowConfigs_[i].maxDebtCeiling ||
                // can not set expand duration to 0 as that could cause a division by 0 in LiquidityCalcs.
                // having expand duration as 0 is anyway not an expected config so removing the possibility for that.
                // if no expansion is wanted, simply set expandDuration to 1 and expandPercent to 0.
                userBorrowConfigs_[i].expandDuration == 0
            ) {
                revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidParams);
            }
            if (userBorrowConfigs_[i].expandPercent > X14) {
                // expandPercent is max 14 bits
                revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__EXPAND_PERCENT_BORROW);
            }
            if (userBorrowConfigs_[i].expandDuration > X24) {
                // duration is max 24 bits
                revert FluidLiquidityError(ErrorTypes.AdminModule__ValueOverflow__EXPAND_DURATION_BORROW);
            }
            if (userBorrowConfigs_[i].baseDebtCeiling == 0 || userBorrowConfigs_[i].maxDebtCeiling == 0) {
                // limits can not be 0. As a side effect, this ensures that there is no borrow config
                // where all values would be 0, so configured users can be differentiated in the mapping.
                revert FluidLiquidityError(ErrorTypes.AdminModule__LimitZero);
            }
            // @dev baseDebtCeiling & maxDebtCeiling have no max bits amount as they are in normal token amount
            // and then converted to BigNumber

            if (
                // base debt ceiling mast be <= max debt ceiling. Also covers case where max = 0 but base > 0
                userBorrowConfigs_[i].baseDebtCeiling > userBorrowConfigs_[i].maxDebtCeiling ||
                // can not set expand duration zero when expansion is possible (when max > base and expandPercentage is not 0),
                // as this is very likely a mistake. there is no point to such a config, as expansion could happen looped instantly.
                // just directly increase base debt ceiling to desired max value if such a config outcome is wanted.
                (userBorrowConfigs_[i].maxDebtCeiling > userBorrowConfigs_[i].baseDebtCeiling &&
                    userBorrowConfigs_[i].expandPercent > 0 &&
                    userBorrowConfigs_[i].expandDuration == 0) ||
                // sanity check that max borrow limit can never be more than 10x the total token supply.
                // protects against that even if someone could artificially inflate token supply to a point where
                // Fluid precision trade-offs could become problematic, can not inflate too much.
                (userBorrowConfigs_[i].maxDebtCeiling >
                    (
                        userBorrowConfigs_[i].token == NATIVE_TOKEN_ADDRESS
                            ? NATIVE_TOKEN_MAX_BORROW_LIMIT_CAP
                            : 10 * IERC20(userBorrowConfigs_[i].token).totalSupply()
                    ))
            ) {
                revert FluidLiquidityError(ErrorTypes.AdminModule__InvalidParams);
            }

            // convert base and max debt limit to BigNumber for storage (10 | 8)
            userBorrowConfigs_[i].maxDebtCeiling = userBorrowConfigs_[i].maxDebtCeiling.toBigNumber(
                SMALL_COEFFICIENT_SIZE,
                DEFAULT_EXPONENT_SIZE,
                BigMathMinified.ROUND_DOWN // no borrowing ever possible above maxDebtCeiling amount
            );
            userBorrowConfigs_[i].baseDebtCeiling = userBorrowConfigs_[i].baseDebtCeiling.toBigNumber(
                SMALL_COEFFICIENT_SIZE,
                DEFAULT_EXPONENT_SIZE,
                BigMathMinified.ROUND_DOWN // up until baseDebtCeiling amount, borrow is always possible
            );

            // get current user config data from storage
            userBorrowData_ = _userBorrowData[userBorrowConfigs_[i].user][userBorrowConfigs_[i].token];

            // if userBorrowData_ == 0 (new setup) or if mode is unchanged, normal update is possible.
            // else if mode changes, values have to be converted from raw <> normal etc.
            if (
                userBorrowData_ == 0 ||
                (userBorrowData_ & 1 == 0 && userBorrowConfigs_[i].mode == 0) ||
                (userBorrowData_ & 1 == 1 && userBorrowConfigs_[i].mode == 1)
            ) {
                // Updating user data on storage

                _userBorrowData[userBorrowConfigs_[i].user][userBorrowConfigs_[i].token] =
                    // mask to update first bit (mode) + bits 162-235 (debt limit values)
                    (userBorrowData_ & 0xfffff0000000000000000003fffffffffffffffffffffffffffffffffffffffe) |
                    (userBorrowConfigs_[i].mode) |
                    (userBorrowConfigs_[i].expandPercent << LiquiditySlotsLink.BITS_USER_BORROW_EXPAND_PERCENT) |
                    (userBorrowConfigs_[i].expandDuration << LiquiditySlotsLink.BITS_USER_BORROW_EXPAND_DURATION) |
                    (userBorrowConfigs_[i].baseDebtCeiling << LiquiditySlotsLink.BITS_USER_BORROW_BASE_BORROW_LIMIT) |
                    (userBorrowConfigs_[i].maxDebtCeiling << LiquiditySlotsLink.BITS_USER_BORROW_MAX_BORROW_LIMIT);
            } else {
                // mode changes -> values have to be converted from raw <> normal etc.

                // if the mode changes then update _exchangePricesAndConfig related data in storage always
                // update exchange prices timely before applying changes that affect utilization, rate etc.
                _updateExchangePrices(userBorrowConfigs_[i].token);

                // get updated exchange prices for the token
                (, borrowExchangePrice_) = LiquidityCalcs.calcExchangePrices(
                    _exchangePricesAndConfig[userBorrowConfigs_[i].token]
                );

                totalAmounts_ = _totalAmounts[userBorrowConfigs_[i].token];
                totalBorrowRawInterest_ = BigMathMinified.fromBigNumber(
                    (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_BORROW_WITH_INTEREST) & X64,
                    DEFAULT_EXPONENT_SIZE,
                    DEFAULT_EXPONENT_MASK
                );
                totalBorrowInterestFree_ = BigMathMinified.fromBigNumber(
                    // no & mask needed for borrow interest free as it occupies the last bits in the storage slot
                    (totalAmounts_ >> LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_BORROW_INTEREST_FREE),
                    DEFAULT_EXPONENT_SIZE,
                    DEFAULT_EXPONENT_MASK
                );

                // read current user borrowing & borrow limit values
                borrowingConversion_ = (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_AMOUNT) & X64; // here borrowingConversion_ = user borrow amount
                borrowingConversion_ =
                    (borrowingConversion_ >> DEFAULT_EXPONENT_SIZE) <<
                    (borrowingConversion_ & DEFAULT_EXPONENT_MASK);

                debtCeilingConversion_ =
                    (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_PREVIOUS_BORROW_LIMIT) &
                    X64; // here debtCeilingConversion_ = previous user borrow limit
                debtCeilingConversion_ =
                    (debtCeilingConversion_ >> DEFAULT_EXPONENT_SIZE) <<
                    (debtCeilingConversion_ & DEFAULT_EXPONENT_MASK);

                // conversion of balance and limit according to the mode change
                if (userBorrowData_ & 1 == 0 && userBorrowConfigs_[i].mode == 1) {
                    // Changing balance from interest free to with interest -> normal amounts to raw amounts
                    // -> must divide by exchange price.

                    // decreasing interest free total borrow; total = total - user borrow
                    totalBorrowInterestFree_ = totalBorrowInterestFree_ > borrowingConversion_
                        ? totalBorrowInterestFree_ - borrowingConversion_
                        : 0;

                    // round up for user borrow amount
                    borrowingConversion_ = FixedPointMathLib.mulDivUp(
                        borrowingConversion_,
                        EXCHANGE_PRICES_PRECISION,
                        borrowExchangePrice_
                    );
                    debtCeilingConversion_ =
                        (debtCeilingConversion_ * EXCHANGE_PRICES_PRECISION) /
                        borrowExchangePrice_;

                    // increasing raw (with interest) total borrow
                    totalBorrowRawInterest_ += borrowingConversion_;
                } else if (userBorrowData_ & 1 == 1 && userBorrowConfigs_[i].mode == 0) {
                    // Changing balance from with interest to interest free-> raw amounts to normal amounts
                    // -> must multiply by exchange price.

                    // decreasing raw (with interest) borrow; total = total - user borrow raw
                    totalBorrowRawInterest_ = totalBorrowRawInterest_ > borrowingConversion_
                        ? totalBorrowRawInterest_ - borrowingConversion_
                        : 0;

                    // round up for user borrow amount
                    borrowingConversion_ = FixedPointMathLib.mulDivUp(
                        borrowingConversion_,
                        borrowExchangePrice_,
                        EXCHANGE_PRICES_PRECISION
                    );
                    debtCeilingConversion_ =
                        (debtCeilingConversion_ * borrowExchangePrice_) /
                        EXCHANGE_PRICES_PRECISION;

                    // increasing interest free total borrow
                    totalBorrowInterestFree_ += borrowingConversion_;
                }

                // change new converted amounts to BigNumber for storage
                borrowingConversion_ = borrowingConversion_.toBigNumber(
                    DEFAULT_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_UP
                );
                debtCeilingConversion_ = debtCeilingConversion_.toBigNumber(
                    DEFAULT_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_DOWN
                );

                // Updating user data on storage
                _userBorrowData[userBorrowConfigs_[i].user][userBorrowConfigs_[i].token] =
                    // mask to update bits 0-128 and bits 162-235 (all except last process timestamp)
                    (userBorrowData_ & 0xfffff0000000000000000003fffffffe00000000000000000000000000000000) |
                    (userBorrowConfigs_[i].mode) |
                    (borrowingConversion_ << LiquiditySlotsLink.BITS_USER_BORROW_AMOUNT) | // BigNumber converted can not overflow
                    (debtCeilingConversion_ << LiquiditySlotsLink.BITS_USER_BORROW_PREVIOUS_BORROW_LIMIT) | // BigNumber converted can not overflow
                    (userBorrowConfigs_[i].expandPercent << LiquiditySlotsLink.BITS_USER_BORROW_EXPAND_PERCENT) |
                    (userBorrowConfigs_[i].expandDuration << LiquiditySlotsLink.BITS_USER_BORROW_EXPAND_DURATION) |
                    (userBorrowConfigs_[i].baseDebtCeiling << LiquiditySlotsLink.BITS_USER_BORROW_BASE_BORROW_LIMIT) |
                    (userBorrowConfigs_[i].maxDebtCeiling << LiquiditySlotsLink.BITS_USER_BORROW_MAX_BORROW_LIMIT);

                // change new total amounts to BigNumber for storage
                totalBorrowRawInterest_ = totalBorrowRawInterest_.toBigNumber(
                    DEFAULT_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_UP
                );
                totalBorrowInterestFree_ = totalBorrowInterestFree_.toBigNumber(
                    DEFAULT_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_UP
                );

                // Updating total borrowings on storage
                _totalAmounts[userBorrowConfigs_[i].token] =
                    // mask to set bits 128-255
                    (totalAmounts_ & 0x00000000000000000000000000000000ffffffffffffffffffffffffffffffff) |
                    (totalBorrowRawInterest_ << LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_BORROW_WITH_INTEREST) | // BigNumber converted can not overflow
                    (totalBorrowInterestFree_ << LiquiditySlotsLink.BITS_TOTAL_AMOUNTS_BORROW_INTEREST_FREE); // BigNumber converted can not overflow

                // trigger update borrow rate, utilization, ratios etc.
                _updateExchangePricesAndRates(userBorrowConfigs_[i].token);
            }

            unchecked {
                ++i;
            }
        }

        emit LogUpdateUserBorrowConfigs(userBorrowConfigs_);
    }
}

/// @notice Fluid Liquidity Guardians only related methods
abstract contract GuardianModule is AuthModule {
    /// @dev only guardians guard
    modifier onlyGuardians() {
        if (_isGuardian[msg.sender] & 1 != 1 && _getGovernanceAddr() != msg.sender) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__OnlyGuardians);
        }
        _;
    }

    /// @inheritdoc ILiquidityAdmin
    function pauseUser(
        address user_,
        address[] calldata supplyTokens_,
        address[] calldata borrowTokens_
    ) public onlyGuardians {
        _checkIsContractOrNativeAddress(user_);
        if (_userClass[user_] == 1) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__UserNotPausable);
        }

        uint256 userData_;

        // pause supply tokens
        uint256 length_ = supplyTokens_.length;

        if (length_ > 0) {
            for (uint256 i; i < length_; ) {
                _checkIsContractOrNativeAddress(supplyTokens_[i]);
                // userData_ => userSupplyData_
                userData_ = _userSupplyData[user_][supplyTokens_[i]];
                if (userData_ == 0) {
                    revert FluidLiquidityError(ErrorTypes.AdminModule__UserNotDefined);
                }
                // set last bit of _userSupplyData (pause flag) to 1
                _userSupplyData[user_][supplyTokens_[i]] =
                    userData_ |
                    (1 << LiquiditySlotsLink.BITS_USER_SUPPLY_IS_PAUSED);

                unchecked {
                    ++i;
                }
            }
        }

        // pause borrow tokens
        length_ = borrowTokens_.length;

        if (length_ > 0) {
            for (uint256 i; i < length_; ) {
                _checkIsContractOrNativeAddress(borrowTokens_[i]);
                // userData_ => userBorrowData_
                userData_ = _userBorrowData[user_][borrowTokens_[i]];
                if (userData_ == 0) {
                    revert FluidLiquidityError(ErrorTypes.AdminModule__UserNotDefined);
                }
                // set last bit of _userBorrowData (pause flag) to 1
                _userBorrowData[user_][borrowTokens_[i]] =
                    userData_ |
                    (1 << LiquiditySlotsLink.BITS_USER_BORROW_IS_PAUSED);

                unchecked {
                    ++i;
                }
            }
        }

        emit LogPauseUser(user_, supplyTokens_, borrowTokens_);
    }

    /// @inheritdoc ILiquidityAdmin
    function unpauseUser(
        address user_,
        address[] calldata supplyTokens_,
        address[] calldata borrowTokens_
    ) public onlyGuardians {
        _checkIsContractOrNativeAddress(user_);

        uint256 userData_;

        // unpause supply tokens
        uint256 length_ = supplyTokens_.length;

        if (length_ > 0) {
            for (uint256 i; i < length_; ) {
                _checkIsContractOrNativeAddress(supplyTokens_[i]);
                // userData_ => userSupplyData_
                userData_ = _userSupplyData[user_][supplyTokens_[i]];
                if ((userData_ >> LiquiditySlotsLink.BITS_USER_SUPPLY_IS_PAUSED) & 1 != 1) {
                    revert FluidLiquidityError(ErrorTypes.AdminModule__UserNotPaused);
                }

                // set last bit of _userSupplyData (pause flag) to 0
                _userSupplyData[user_][supplyTokens_[i]] =
                    userData_ &
                    0x7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

                unchecked {
                    ++i;
                }
            }
        }

        // unpause borrow tokens
        length_ = borrowTokens_.length;

        if (length_ > 0) {
            for (uint256 i; i < length_; ) {
                _checkIsContractOrNativeAddress(borrowTokens_[i]);
                // userData_ => userBorrowData_
                userData_ = _userBorrowData[user_][borrowTokens_[i]];
                if ((userData_ >> LiquiditySlotsLink.BITS_USER_BORROW_IS_PAUSED) & 1 != 1) {
                    revert FluidLiquidityError(ErrorTypes.AdminModule__UserNotPaused);
                }
                // set last bit of _userBorrowData (pause flag) to 0
                _userBorrowData[user_][borrowTokens_[i]] =
                    userData_ &
                    0x7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

                unchecked {
                    ++i;
                }
            }
        }

        emit LogUnpauseUser(user_, supplyTokens_, borrowTokens_);
    }
}

/// @title Fluid Liquidity AdminModule
/// @notice Fluid Liquidity auth protected methods to configure things such as:
/// guardians, auths, governance, revenue, token configs, allowances etc.
/// Accessibility of methods is restricted to Governance, Auths or Guardians. Governance is Auth & Governance by default
contract AdminModule is AdminModuleConstants, GuardianModule {
    constructor(uint256 nativeTokenMaxBorrowLimitCap_) AdminModuleConstants(nativeTokenMaxBorrowLimitCap_) {}

    /// @inheritdoc ILiquidityAdmin
    function collectRevenue(address[] calldata tokens_) external {
        address payable revenueCollector_ = payable(_revenueCollector);
        if (revenueCollector_ == address(0)) {
            revert FluidLiquidityError(ErrorTypes.AdminModule__RevenueCollectorNotSet);
        }

        uint256 length_ = tokens_.length;
        for (uint256 i; i < length_; ) {
            _checkIsContractOrNativeAddress(tokens_[i]);

            bool isNativeToken_ = tokens_[i] == NATIVE_TOKEN_ADDRESS;

            // get revenue amount with updated interest etc.
            uint256 revenueAmount_ = LiquidityCalcs.calcRevenue(
                _totalAmounts[tokens_[i]],
                _exchangePricesAndConfig[tokens_[i]],
                isNativeToken_ ? address(this).balance : IERC20(tokens_[i]).balanceOf(address(this))
            );

            if (revenueAmount_ > 0) {
                // transfer token amount to revenueCollector address
                if (isNativeToken_) {
                    Address.sendValue(revenueCollector_, revenueAmount_);
                } else {
                    SafeERC20.safeTransfer(IERC20(tokens_[i]), revenueCollector_, revenueAmount_);
                }
            }

            emit LogCollectRevenue(tokens_[i], revenueAmount_);

            unchecked {
                ++i;
            }
        }
    }

    /// @inheritdoc ILiquidityAdmin
    function updateExchangePrices(
        address[] calldata tokens_
    ) external returns (uint256[] memory supplyExchangePrices_, uint256[] memory borrowExchangePrices_) {
        uint256 tokensLength_ = tokens_.length;

        supplyExchangePrices_ = new uint256[](tokensLength_);
        borrowExchangePrices_ = new uint256[](tokensLength_);

        for (uint256 i; i < tokensLength_; ) {
            _checkIsContractOrNativeAddress(tokens_[i]);
            (supplyExchangePrices_[i], borrowExchangePrices_[i]) = _updateExchangePricesAndRates(tokens_[i]);

            unchecked {
                ++i;
            }
        }
    }
}
